import React, { Component } from 'react';
import Categories from "./Categories";

class CategoriesBlock extends Component {

	apiCall(url, handler){
        fetch(url)
        .then( response => response.json() )
        .then( data => handler(data) )
        .catch( e => console.log(e) )
    }

	componentDidMount(){
        this.apiCall("https://grupo10dh.herokuapp.com/api/products/getCategories", this.mostrarNumero)
    }

	mostrarNumero = (data) => {
        
		let categorias;

		for(let i = 0; i < data.data.count; i++){
			categorias = data.data.rows[i].type
		}

		this.setState({
			categoria: categorias
		})
        
    }


	render (){
		return (
			<div className="col-lg-6 mb-4">						
				<div className="card shadow mb-4">
					<div className="card-header py-3">
						<h6 className="m-0 font-weight-bold text-primary">Categories in Data Base</h6>
					</div>
				<div className="card-body">
					<div className="row">
						<Categories categoria = {this.state.categoria} />
						<Categories categoria = {this.state.categoria} />
						<Categories categoria = {this.state.categoria} />
					</div>
				</div>
				</div>
			</div>
		);
	}
}

export default CategoriesBlock;
