import React, { Component } from 'react';

class LastProduct extends Component {

	constructor(props){
        super(props);
        this.state = {
            numero: []
        }
    }

    apiCall(url, handler){
        fetch(url)
        .then( response => response.json() )
        .then( data => handler(data) )
        .catch( e => console.log(e) )
    }

    componentDidMount(){
        this.apiCall("https://grupo10dh.herokuapp.com/api/products/getProducts", this.mostrarProducto)
    }

    mostrarProducto = (data) => {
		let array = data.data.rows;
		let ultimoProducto = array[array.length - 2]

		this.setState({
			nombre: ultimoProducto.nombre,
			imagen: "../../imgProducts/product.jpg",
			detalle: ultimoProducto.detalle,
			precio: ultimoProducto.precio,
		})
    }
  
	render(){
		return (
	<div className="col-lg-6 mb-4">
		<div className="card shadow mb-4">
			<div className="card-header py-3">
				<h6 className="m-0 font-weight-bold text-primary">Last product in Data Dase</h6>
			</div>
			<div className="card-body">
			    <p>{this.state.nombre}</p>
				<div className="text-center">
					<img className="img-fluid px-3 px-sm-4 mt-3 mb-4" style={{width: "25rem"}} src={this.state.imagen} alt="lastProduct"/>
				</div>
				<p>{this.state.detalle}</p>
				<p>$ {this.state.precio}</p>
			</div>
		</div>
	</div>
  );
}

}

export default LastProduct;
