import React, { Component } from 'react';

class Categories extends Component {

	//Estado por defecto de categoria
	constructor(props){
        super(props);
        this.state = {
            cantidad: ""
        }
    }

	//Utilizo fetch dentro del Metodo ApiCall
	apiCall(url, handler){
        fetch(url)
        .then( response => response.json() )
        .then( data => handler(data) )
        .catch( e => console.log(e) )
    }

	//Llamo dentro del componentDidMount al metodo apiCall usando la URL de la API conrrespondiente y genero una respuesta con mostrarDatos
    componentDidMount(){
        this.apiCall("https://grupo10dh.herokuapp.com/api/products/getCategoriesWithProducts/1", this.mostrarCantidad1)
		this.apiCall("https://grupo10dh.herokuapp.com/api/products/getCategoriesWithProducts/2", this.mostrarCantidad2)
		this.apiCall("https://grupo10dh.herokuapp.com/api/products/getCategoriesWithProducts/3", this.mostrarCantidad3)
    }

	//mostrarDatos devuelve los datos de la API mediante data
	mostrarCantidad1 = (data) => {

		//Defino el nuevo estado de categoria con el array de las categorias
		this.setState({
			cantidad: data.data.count
		})
	}

	mostrarCantidad2 = (data) => {

		//Defino el nuevo estado de categoria con el array de las categorias
		this.setState({
			cantidad: data.data.count
		})
	}

	mostrarCantidad3 = (data) => {

		//Defino el nuevo estado de categoria con el array de las categorias
		this.setState({
			cantidad: data.data.count
		})
	}

	// Utilizo props para asignarle una propiedad que luego va a ser modificada en el componente CATEGORIESBLOCK
	render (){
		return (
			<div className="col-lg-6 mb-4">
				<div className="card bg-info text-white shadow">
					<div className="card-body">
						<p>{this.props.categoria}</p>
						<p>Qty: {this.state.cantidad}</p>
					</div>
				</div>
			</div>
		);
	}
	
}

export default Categories;
