import React, { Component } from 'react';
import Categories from "./Categories";

class CategoriesBlock extends Component {

	constructor(props){
        super(props);
        this.state = {
            numero: ""
        }
    }

    apiCall(url, handler){
        fetch(url)
        .then( response => response.json() )
        .then( data => handler(data) )
        .catch( e => console.log(e) )
    }

    componentDidMount(){
        console.log("componentDidMount")
        this.apiCall("https://grupo10dh.herokuapp.com/api/products/getCategories", this.mostrarNumero)
    }

    mostrarNumero = (data) => {
		
		let categoria = "";
		let totalCategorias = data.data.count;

		for( let i = 0; i < totalCategorias; i++){
			if(totalCategorias != 0){
				categoria = <Categories />;
			}else{
				categoria = "No existen categorias disponibles"
			}
		};

		this.setState ({
			categoria : <Categories /> * data.data.count
		})
        console.log(data.data.count)
    }


  render () {
	  return (
	<div className="col-lg-6 mb-4">						
	    <div className="card shadow mb-4">
			<div className="card-header py-3">
				<h6 className="m-0 font-weight-bold text-primary">Categories in Data Base</h6>
			</div>
		<div className="card-body">
			<div className="row">
				{this.state.categoria}
			</div>
		</div>
		</div>
	</div>
	  )
  };

}

export default CategoriesBlock;
