import React, { Component } from 'react';

class TopbarNav extends Component {

	constructor(props){
        super(props);
        this.state = {
            usuario: []
        }
    }

    apiCall(url, handler){
        fetch(url)
        .then( response => response.json() )
        .then( data => handler(data) )
        .catch( e => console.log(e) )
    }

    componentDidMount(){
        this.apiCall("https://grupo10dh.herokuapp.com/api/users/getUsers", this.mostrarUsuario)
    }

    mostrarUsuario = (data) => {
        console.log(data)
    }

  render(){
	  return (
	<ul className="navbar-nav ml-auto">
		<li className="nav-item dropdown no-arrow mx-1">
			<a className="nav-link dropdown-toggle" href="/" id="alertsDropdown">
    			<i className="fas fa-bell fa-fw"></i>
	    		<span className="badge badge-danger badge-counter">3+</span>
			</a>
		</li>
		<li className="nav-item dropdown no-arrow mx-1">
			<a className="nav-link dropdown-toggle" href="/" id="messagesDropdown">
		    	<i className="fas fa-envelope fa-fw"></i>
				<span className="badge badge-danger badge-counter">7</span>
			</a>
		</li>

		<div className="topbar-divider d-none d-sm-block"></div>

			<li className="nav-item dropdown no-arrow">
				<a className="nav-link dropdown-toggle" href="/" id="userDropdown">
					<span className="mr-2 d-none d-lg-inline text-gray-600 small">Walter White</span>
		    		<img alt="userIcon" className="img-profile rounded-circle" src="assets/images/dummy-avatar.jpg" width="60"/>
				</a>
			</li>

	</ul>
  );
}

}

export default TopbarNav;
